		include "macros.asm"
		include "Equates.asm"

; Assembly option
RemovePadding = 0
; If this is set to 1, this removes unnecessary 'nops' also removes leftovers.

     	     	org 0		; to make sure the start of the ROM is always 0
Header:		dc.l $FFFFFE00,EntryPoint,BusError,AddressError; 0 ; test Port A Ctrl
		dc.l IllegalInstr,ZeroDivide,ChkInstr,TrapvInstr; 4
		dc.l PriviledgeViolation,Trace,Line1010Emu,Line1111Emu;	8
		dc.l ErrorException,ErrorException,ErrorException,ErrorException; 12
		dc.l ErrorException,ErrorException,ErrorException,ErrorException; 16
		dc.l ErrorException,ErrorException,ErrorException,ErrorException; 20
		dc.l ErrorException,ErrorTrap,ErrorTrap,ErrorTrap; 24
		dc.l VBlank,ErrorTrap,HBlank,ErrorTrap;	28
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 32
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 36
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 40
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 44
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 48
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 52
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 56
		dc.l ErrorTrap,ErrorTrap,ErrorTrap,ErrorTrap; 60
		dc.b 'SEGA MEGA DRIVE ' ; Console name
		dc.b '(C)SEGA 1991.APR' ; Release date (Sonic 1)
		dc.b 'SONIC THE             HEDGEHOG 2                ' ; Domestic name
		dc.b 'SONIC THE             HEDGEHOG 2                ' ; International name
		dc.b 'GM 00004049-01'   ; Serial/version number (Sonic 1)
Checksum:	dc.w $811C		; ROM Checksum
		dc.b 'J               ' ; I/O support (3-button controller)
ROMStart:	dc.l Header		; ROM start location
	if RemovePadding=0
ROMEnd:		dc.l $7FFFF		; ROM end location (512 KB - Sonic 1)
	else
ROMEnd:		dc.l EndOfROM-1		; ROM end location
	endif
	     	dc.l RAM_Start&$FFFFFF	; RAM start
	     	dc.l (RAM_End)&$FFFFFF; RAM end
		dc.l $20202020		; SRAM support (none)
		dc.l $20202020
		dc.l $20202020
		dc.l $20202020
		dc.b '                                                ' ; Notes
		dc.b 'JUE             '	; Region coding (Japan, America, Europe)
EndOfHeader:

	if RemovePadding=0
	else
	    	opt op+
	    	opt os+
	    	opt ow+
	    	opt oz+
	    	opt oaq+
	    	opt osq+
	    	opt omq+
	endif
; ---------------------------------------------------------------------------

ErrorTrap:				; CODE XREF: ROM:00000204j
					; DATA XREF: ROM:00000000o ..
	if RemovePadding=0
		nop
		nop
	endif
		bra.s	ErrorTrap
; ---------------------------------------------------------------------------

EntryPoint:				; DATA XREF: ROM:00000000o
		tst.l	($A10008).l	; test Port A Ctrl
		bne.s	PortA_OK
		tst.w	($A1000C).l     ; test Port C Ctrl

PortA_OK:				; CODE XREF: ROM:0000020Cj
		bne.s	PortC_OK
		lea	InitValues(pc),a5
		movem.w	(a5)+,d5-d7
		movem.l	(a5)+,a0-a4
		move.b	-$10FF(a1),d0	; get hardware version
		andi.b	#$F,d0
		beq.s	SkipSecurity
		move.l	#'SEGA',$2F00(a1)

SkipSecurity:				; CODE XREF: ROM:0000022Aj
		move.w	(a4),d0
		moveq	#0,d0
		movea.l	d0,a6
		move.l	a6,usp
		moveq	#$17,d1

VDPInitLoop:				; CODE XREF: ROM:00000244j
		move.b	(a5)+,d5
		move.w	d5,(a4)
		add.w	d7,d5
		dbf	d1,VDPInitLoop
		move.l	(a5)+,(a4)
		move.w	d0,(a3)
		move.w	d7,(a1)
		move.w	d7,(a2)

WaitForZ80:				; CODE XREF: ROM:00000252j
		btst	d0,(a1)
		bne.s	WaitForZ80
		moveq	#$25,d2	; '%'

Z80InitLoop:				; CODE XREF: ROM:00000258j
		move.b	(a5)+,(a0)+
		dbf	d2,Z80InitLoop
		move.w	d0,(a2)
		move.w	d0,(a1)
		move.w	d7,(a2)

ClearRAMLoop:				; CODE XREF: ROM:loc_264j
		move.l	d0,-(a6)
		dbf	d6,ClearRAMLoop
		move.l	(a5)+,(a4)
		move.l	(a5)+,(a4)
		moveq	#$1F,d3

ClearCRAMLoop:				; CODE XREF: ROM:00000270j
		move.l	d0,(a3)
		dbf	d3,ClearCRAMLoop
		move.l	(a5)+,(a4)
		moveq	#$13,d4

ClearVSRAMLoop:				; CODE XREF: ROM:0000027Aj
		move.l	d0,(a3)
		dbf	d4,ClearVSRAMLoop
		moveq	#3,d5

PSGInitLoop:				; CODE XREF: ROM:00000284j
		move.b	(a5)+,$11(a3)
		dbf	d5,PSGInitLoop
		move.w	d0,(a2)
		movem.l	(a6),d0-a6
		disable_ints

PortC_OK:				; DATA XREF: ROM:PortA_OKt
		bra.s	GameProgram
; ---------------------------------------------------------------------------
InitValues:	dc.w VDPREG_MODE1	; DATA XREF: ROM:00000216t
		dc.w $3FFF
		dc.w $100

		dc.l Z80Ram		; Z80 RAM start	location
		dc.l Z80BusReq		; Z80 bus request
		dc.l Z80Reset		; Z80 reset
		dc.l VdpData		; VDP data port
		dc.l VdpCtrl		; VDP control port

		dc.b %00000100		; VDP $80 - 8-colour mode
		dc.b %00010100		; VDP $81 - Megadrive mode, DMA enable
		dc.b (vram_fg>>10)	; VDP $82 - foreground nametable address
		dc.b (vram_sonic>>10)	; VDP $83 - window nametable address
		dc.b (vram_bg>>13)	; VDP $84 - background nametable address
		dc.b ($D800>>9)		; VDP $85 - sprite table address
		dc.b 0			; VDP $86 - unused
		dc.b 0			; VDP $87 - background colour
		dc.b 0			; VDP $88 - unused
		dc.b 0			; VDP $89 - unused
		dc.b -1			; VDP $8A - HBlank register
		dc.b 0			; VDP $8B - full screen scroll
		dc.b %10000001		; VDP $8C - 40 cell display
		dc.b ($DC00>>10)	; VDP $8D - hscroll table address
		dc.b 0			; VDP $8E - unused
		dc.b 1			; VDP $8F - VDP increment
		dc.b %00000001		; VDP $90 - 64 cell hscroll size
		dc.b 0			; VDP $91 - window h position
		dc.b 0			; VDP $92 - window v position
		dc.w -1			; VDP $93/94 - DMA length
		dc.w 0			; VDP $95/96 - DMA source
		dc.b $80		; VDP $97 - DMA fill VRAM

		dc.l VRAM_DMA_CMD	; value	for VRAM fill

		dc.b $AF		; xor	a
		dc.b $01, $D9, $1F	; ld	bc,1fd9h
		dc.b $11, $27, $00	; ld	de,0027h
		dc.b $21, $26, $00	; ld	hl,0026h
		dc.b $F9		; ld	sp,hl
		dc.b $77		; ld	(hl),a
		dc.b $ED, $B0		; ldir
		dc.b $DD, $E1		; pop	ix
		dc.b $FD, $E1		; pop	iy
		dc.b $ED, $47		; ld	i,a
		dc.b $ED, $4F		; ld	r,a
		dc.b $D1		; pop	de
		dc.b $E1		; pop	hl
		dc.b $F1		; pop	af
		dc.b $08		; ex	af,af'
		dc.b $D9		; exx
		dc.b $C1		; pop	bc
		dc.b $D1		; pop	de
		dc.b $E1		; pop	hl
		dc.b $F1		; pop	af
		dc.b $F9		; ld	sp,hl
		dc.b $F3		; di
		dc.b $ED, $56		; im1
		dc.b $36, $E9		; ld	(hl),e9h
		dc.b $E9		; jp	(hl)

		dc.w VDPREG_MODE2+%000000100	; VDP display mode
		dc.w VDPREG_INCR+2		; VDP increment
		dc.l CRAM_ADDR_CMD		; value	for CRAM Write mode
		dc.l VSRAM_ADDR_CMD		; value	for VSRAM write	mode

		dc.b  -97, -65,	-33, -1; 0 ; values for PSG channel volumes
		even
; ---------------------------------------------------------------------------

GameProgram:				; CODE XREF: ROM:PortC_OKj
		tst.w	(VdpCtrl).l
		btst	#6,(IoCtrlExt).l
	if RemovePadding=0
		beq.s	ChecksumCheck
		cmpi.l	#'init',(Checksum_fourcc).w
		beq.w	AlreadyInit

ChecksumCheck:				; CODE XREF: ROM:0000030Ej
		movea.l	#EndOfHeader,a0
		movea.l	#ROMEnd,a1	; ROM end location (leftover from Sonic	1)
		move.l	(a1),d0
		move.l	#$7FFFF,d0
		moveq	#0,d1

ChksumChkLoop:				; CODE XREF: ROM:00000336j
		add.w	(a0)+,d1
		cmp.l	a0,d0
		bcc.s	ChksumChkLoop
		movea.l	#Checksum,a1	; ROM Checksum (leftover from Sonic 1)
		cmp.w	(a1),d1
		nop
		nop
	endif
		lea	($FFFFFE00).w,a6
		moveq	#0,d7
		move.w	#$7F,d6	; ''

ClearSomeRAMLoop:			; CODE XREF: ROM:00000350j
		move.l	d7,(a6)+
		dbf	d6,ClearSomeRAMLoop
		move.b	(Z80Version).l,d0
		andi.b	#$C0,d0
		move.b	d0,(Graphics_Flags).w
	if RemovePadding=0
		move.l	#'init',(Checksum_fourcc).w
	endif

AlreadyInit:				; CODE XREF: ROM:00000318j
		lea	(RAM_Start).l,a6
		moveq	#0,d7
		move.w	#$3F7F,d6

ClearRemainingRAMLoop:			; CODE XREF: ROM:00000378j
		move.l	d7,(a6)+
		dbf	d6,ClearRemainingRAMLoop
		bsr.w	VDPRegSetup
		bsr.w	SoundDriverLoad
		bsr.w	JoypadInit
		move.b	#0,(Game_Mode).w

MainGameLoop:				; CODE XREF: ROM:0000039Aj
		move.b	(Game_Mode).w,d0
		andi.w	#$1C,d0
		jsr	GameModeArray(pc,d0.w)
		bra.s	MainGameLoop
; ---------------------------------------------------------------------------

GameModeArray:
		bra.w	SegaScreen	; $00
		bra.w	TitleScreen	; $04
		bra.w	Level		; $08
		bra.w	Level		; $0C
		bra.w	SpecialStage	; $10
; ---------------------------------------------------------------------------
	if RemovePadding=0
;----------------------------------------------------
;
; ChecksumError
;
; Leftover subroutine from Sonic 1 showing a red
;  screen when the checksum was	wrong
;
;----------------------------------------------------

ChecksumError:
		bsr.w	VDPRegSetup
		move.l	#CRAM_ADDR_CMD,(VdpCtrl).l
		moveq	#$3F,d7	; '?'

ChksumErr_RedFill:			; CODE XREF: ROM:000003C8j
		move.w	#$E,(VdpData).l
		dbf	d7,ChksumErr_RedFill

ChksumErr_InfLoop:			; CODE XREF: ROM:ChksumErr_InfLoopj
		bra.s	ChksumErr_InfLoop
	endif
; ---------------------------------------------------------------------------

BusError:				; DATA XREF: ROM:00000000o
		move.b	#2,($FFFFFC44).w
		bra.s	ErrorMsg_TwoAddresses
; ---------------------------------------------------------------------------

AddressError:				; DATA XREF: ROM:00000000o
		move.b	#4,($FFFFFC44).w
		bra.s	ErrorMsg_TwoAddresses
; ---------------------------------------------------------------------------

IllegalInstr:				; DATA XREF: ROM:00000000o
		move.b	#6,($FFFFFC44).w
		addq.l	#2,2(sp)
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

ZeroDivide:				; DATA XREF: ROM:00000000o
		move.b	#8,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

ChkInstr:				; DATA XREF: ROM:00000000o
		move.b	#$A,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

TrapvInstr:				; DATA XREF: ROM:00000000o
		move.b	#$C,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

PriviledgeViolation:			; DATA XREF: ROM:00000000o
		move.b	#$E,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

Trace:					; DATA XREF: ROM:00000000o
		move.b	#$10,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

Line1010Emu:				; DATA XREF: ROM:00000000o
		move.b	#$12,($FFFFFC44).w
		addq.l	#2,2(sp)
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

Line1111Emu:				; DATA XREF: ROM:00000000o
		move.b	#$14,($FFFFFC44).w
		addq.l	#2,2(sp)
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

ErrorException:				; DATA XREF: ROM:00000000o
		move.b	#0,($FFFFFC44).w
		bra.s	ErrorMessage
; ---------------------------------------------------------------------------

ErrorMsg_TwoAddresses:			; CODE XREF: ROM:000003D4j
					; ROM:000003DCj
		disable_ints
		addq.w	#2,sp
		move.l	(sp)+,($FFFFFC40).w
		addq.w	#2,sp
		movem.l	d0-a7,($FFFFFC00).w
		bsr.w	ShowErrorMsg
		move.l	2(sp),d0
		bsr.w	ShowErrAddress
		move.l	($FFFFFC40).w,d0
		bsr.w	ShowErrAddress
		bra.s	ErrorMsg_Wait
; ---------------------------------------------------------------------------

ErrorMessage:				; CODE XREF: ROM:000003E8j
					; ROM:000003F0j ...
		disable_ints
		movem.l	d0-a7,($FFFFFC00).w
		bsr.w	ShowErrorMsg
		move.l	2(sp),d0
		bsr.w	ShowErrAddress

ErrorMsg_Wait:				; CODE XREF: ROM:00000458j
		bsr.w	Error_WaitForC
		movem.l	($FFFFFC00).w,d0-a7
		enable_ints
		rte

; ~~~~~~~~~~~~~~~ S U B	R O U T	I N E ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


ShowErrorMsg:				; CODE XREF: ROM:00000444p
					; ROM:00000464p
		lea	(VdpData).l,a6
		move.l	#$78000003,(VdpCtrl).l
		lea	(Art_Text).l,a0
		move.w	#$27F,d1

Error_LoadGfx:				; CODE XREF: ShowErrorMsg+1Cj
		move.w	(a0)+,(a6)
		dbf	d1,Error_LoadGfx
		moveq	#0,d0
		move.b	($FFFFFC44).w,d0

loc_4A6:
		move.w	ErrorText(pc,d0.w),d0
		lea	ErrorText(pc,d0.w),a0
		move.l	#$46040003,(VdpCtrl).l
		moveq	#$12,d1

Error_CharsLoop:			; CODE XREF: ShowErrorMsg+44j
		moveq	#0,d0
		move.b	(a0)+,d0
		addi.w	#$790,d0
		move.w	d0,(a6)
		dbf	d1,Error_CharsLoop
		rts
; End of function ShowErrorMsg

; ---------------------------------------------------------------------------
ErrorText:	dc.w ErrText_Exept-ErrorText	; "ERROR EXCEPTION    "
		dc.w ErrText_BusErr-ErrorText 	;	"BUS ERROR	    "
		dc.w ErrText_AddrErr-ErrorText 	; "ADDRESS ERROR	     "
		dc.w ErrText_IllInstr-ErrorText	; "ILLEGAL INSTRUCTION"
		dc.w ErrText_ZeroDiv-ErrorText 	; "@ERO DIVIDE	     "
		dc.w ErrText_ChkInstr-ErrorText	; "CHK INSTRUCTION    "
		dc.w ErrText_TrapV-ErrorText 	; "TRAPV INSTRUCTION  "
		dc.w ErrText_PrivViol-ErrorText	; "PRIVILEGE VIOLATION"
		dc.w ErrText_Trace-ErrorText 	; "TRACE		   "
		dc.w ErrText_Line1010-ErrorText	; "LINE	1010 EMULATOR "
		dc.w ErrText_Line1111-ErrorText	; "LINE	1111 EMULATOR "
ErrText_Exept:	dc.b 'ERROR EXCEPTION    ' ; DATA XREF: ROM:ErrorTexto
ErrText_BusErr:	dc.b 'BUS ERROR          ' ; DATA XREF: ROM:000004CCo
ErrText_AddrErr:dc.b 'ADDRESS ERROR      ' ; DATA XREF: ROM:000004CEo
ErrText_IllInstr:dc.b 'ILLEGAL INSTRUCTION' ; DATA XREF: ROM:000004D0o
ErrText_ZeroDiv:dc.b '@ERO DIVIDE        ' ; DATA XREF: ROM:000004D2o
ErrText_ChkInstr:dc.b 'CHK INSTRUCTION    ' ; DATA XREF: ROM:000004D4o
ErrText_TrapV:	dc.b 'TRAPV INSTRUCTION  ' ; DATA XREF: ROM:000004D6o
ErrText_PrivViol:dc.b 'PRIVILEGE VIOLATION' ; DATA XREF: ROM:000004D8o
ErrText_Trace:	dc.b 'TRACE              ' ; DATA XREF: ROM:000004DAo
ErrText_Line1010:dc.b 'LINE 1010 EMULATOR ' ; DATA XREF: ROM:000004DCo
ErrText_Line1111:dc.b 'LINE 1111 EMULATOR ' ; DATA XREF: ROM:000004DEo
		 even

; ~~~~~~~~~~~~~~~ S U B	R O U T	I N E ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


ShowErrAddress:				; CODE XREF: ROM:0000044Cp
					; ROM:00000454p ...
		move.w	#$7CA,(a6)
		moveq	#7,d2

ShowErrAddress_DigitLoop:		; CODE XREF: ShowErrAddress+Aj
		rol.l	#4,d0
		bsr.s	ShowErrDigit
		dbf	d2,ShowErrAddress_DigitLoop
		rts
; End of function ShowErrAddress


; ~~~~~~~~~~~~~~~ S U B	R O U T	I N E ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


ShowErrDigit:				; CODE XREF: ShowErrAddress+8p
		move.w	d0,d1
		andi.w	#$F,d1
		cmpi.w	#$A,d1
		bcs.s	ShowErrDigit_NoOverflow
		addq.w	#7,d1

ShowErrDigit_NoOverflow:		; CODE XREF: ShowErrDigit+Aj
		addi.w	#$7C0,d1
		move.w	d1,(a6)
		rts
; End of function ShowErrDigit


; ~~~~~~~~~~~~~~~ S U B	R O U T	I N E ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


Error_WaitForC:				; CODE XREF: ROM:ErrorMsg_Waitp
					; Error_WaitForC+Aj
		bsr.w	ReadJoypads
		cmpi.b	#$20,($FFFFF605).w ; ' '
		bne.w	Error_WaitForC
		rts
; End of function Error_WaitForC

; ---------------------------------------------------------------------------
Art_Text:	incbin	art/text.bin
		even
; ---------------------------------------------------------------------------

HBlank:					; DATA XREF: ROM:00000000o
		movem.l	d0-a6,-(sp)
		tst.b	($FFFFF62A).w
		beq.s	loc_B86

loc_B12:				; CODE XREF: ROM:00000B1Cj
		move.w	(VdpCtrl).l,d0
		andi.w	#8,d0
		beq.s	loc_B12
		move.l	#VSRAM_ADDR_CMD,(VdpCtrl).l
		move.l	($FFFFF616).w,(VdpData).l
		btst	#6,(Graphics_Flags).w
		beq.s	loc_B40
		move.w	#$700,d0

loc_B3C:				; CODE XREF: ROM:loc_B3Cj
		dbf	d0,loc_B3C

loc_B40:				; CODE XREF: ROM:00000B36j
		move.b	($FFFFF62A).w,d0
		move.b	#0,($FFFFF62A).w
		move.w	#1,($FFFFF644).w
		andi.w	#$3E,d0	; '>'
		move.w	off_B6C(pc,d0.w),d0
		jsr	off_B6C(pc,d0.w)

loc_B5C:				; CODE XREF: ROM:00000B9Cj
					; ROM:00000C3Aj ...
		jsr	sub_71B4C

loc_B62:				; CODE XREF: ROM:00000DE2j
		addq.l	#1,($FFFFFE0C).w
		movem.l	(sp)+,d0-a6
		rte
; ---------------------------------------------------------------------------
off_B6C:	dc.w loc_B86-off_B6C,loc_CAA-off_B6C; 0	; DATA XREF: ROM:off_B6Co
					; ROM:off_B6C+2o ...
		dc.w loc_CBC-off_B6C,loc_CD2-off_B6C; 2
		dc.w loc_CE2-off_B6C,loc_E02-off_B6C; 4
		dc.w loc_EA2-off_B6C,loc_F88-off_B6C; 6
		dc.w loc_CD8-off_B6C,loc_F98-off_B6C; 8
		dc.w loc_CAE-off_B6C,loc_FA4-off_B6C; 10
		dc.w loc_EA2-off_B6C	; 12
; ---------------------------------------------------------------------------

loc_B86:				; CODE XREF: ROM:00000B10j
					; DATA XREF: ROM:off_B6Co
		cmpi.b	#$8C,(Game_Mode).w
		beq.s	loc_BA0
		cmpi.b	#8,(Game_Mode).w
		beq.s	loc_BA0
		cmpi.b	#$C,(Game_Mode).w
		bne.w	loc_B5C

loc_BA0:				; CODE XREF: ROM:00000B8Cj
					; ROM:00000B94j
		tst.b	($FFFFF730).w
		beq.w	loc_C3E
		move.w	(VdpCtrl).l,d0
		btst	#6,(Graphics_Flags).w
		beq.s	loc_BBE
		move.w	#$700,d0

loc_BBA:				; CODE XREF: ROM:loc_BBAj
		dbf	d0,loc_BBA

loc_BBE:				; CODE XREF: ROM:00000BB4j
		move.w	#1,($FFFFF644).w
		PauseZ80
		tst.b	($FFFFF64E).w
		bne.s	loc_C02
		lea	(VdpCtrl).l,a5
		move.l	#$94009340,(a5)
		move.l	#$96FD9580,(a5)
		move.w	#$977F,(a5)
		move.w	#$C000,(a5)
		move.w	#$80,($FFFFF640).w ; '€'
		move.w	($FFFFF640).w,(a5)
		bra.s	loc_C26
; ---------------------------------------------------------------------------

loc_C02:				; CODE XREF: ROM:00000BDAj
